CREATE TABLE IF NOT EXISTS `book` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `bookname` varchar(200) NOT NULL,
  `author` varchar(100) NOT NULL,
  `authmsg` varchar(500) NOT NULL,
  `bookimg` varchar(200) NOT NULL,
  `summary` varchar(500) NOT NULL,
  `more` varchar(2000) NOT NULL,
  `catalog` varchar(500) NOT NULL,
  `type` varchar(100) NOT NULL DEFAULT 'IT',
  `price` varchar(100) NOT NULL DEFAULT '50',
  `oldprice` varchar(100) NOT NULL DEFAULT '100',
  `pubtime` int(100) NOT NULL DEFAULT '2016',
  `saled` varchar(50) NOT NULL DEFAULT '2',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=15 ;

